import React, { useState } from 'react'
import { useEffect } from 'react'
import axios from "axios"

const Schedules = () => {
    const [schedules, setSchedules] = useState([])

    useEffect(() => {
        const fetchAllSchedules = async ()=>{
          try{
              const res = await axios.get("http://localhost:8802/schedules");
              setSchedules(res.data)
          }catch(err){
              console.log(err)
          }
        }
        fetchAllSchedules()
      }, [])

      return <div class='container'>
      <table class='table'>
          <thead bgcolor='#F47920'>
          <tr>
          <th>Name</th>
          <th>Day</th>
          <th>Time</th>
          <th>Start/End Date</th>
          <th>Base Price</th>
          <th>Member Price</th>
          <th></th>
          </tr>
          </thead>
          <tbody>
          {schedules.map(schedule=>(
              <tr key={schedule.id}>
                  <td>{schedule.name}</td>
                  <td>{schedule.day_of_week}</td>
                  <td>{schedule.start_time}  {schedule.end_time}</td>
                  <td><div>{schedule.start_date.toString().split('T')[0]}  {schedule.end_date.toString().split('T')[0]}</div></td>
                  <td>{schedule.base_price}</td>
                  <td>{schedule.member_price}</td>
                  {
                    document.cookie ? <td><button>Enroll</button></td> : <td></td>
                  }
                  
              </tr>
          ))}

</tbody>
      </table>
    </div>
}

export default Schedules
