import React, { useState } from 'react'
import { useEffect } from 'react'
import axios from "axios"
import Cookies from 'js-cookie'
import {useNavigate} from "react-router-dom"
const Schedules = () => {
    const navigate = useNavigate();

    async function handleClick(e){
        try{
            const prereq_name = await axios.post("http://localhost:8802/prereq", {course_id: e});
            console.log(prereq_name.data[0].prereq_name + "\n");
            const prereq_id = await axios.post("http://localhost:8802/prereq2", {prereq_name: prereq_name.data[0].prereq_name});
            console.log({user_id: JSON.parse(Cookies.get('user_id')).id, course_id: e});
            const result = await axios.post("http://localhost:8802/enrollment", {user_id: JSON.parse(Cookies.get('user_id')).id, course_id: e});
            console.log(e + result)
            navigate("/");
            
          }catch(err){
            console.log(err);
          }
    }

    const [schedules, setSchedules] = useState([])

    useEffect(() => {
        const fetchAllSchedules = async ()=>{
          try{
              const res = await axios.get("http://localhost:8802/schedules");
              setSchedules(res.data)
          }catch(err){
              console.log(err)
          }
        }
        fetchAllSchedules()
      }, [])

      return <div class='container'>
      <table class='table'>
          <thead bgcolor='#F47920'>
          <tr>
          <th>Name</th>
          <th>Day</th>
          <th>Time</th>
          <th>Start/End Date</th>
          <th>Base Price</th>
          <th>Member Price</th>
          <th></th>
          </tr>
          </thead>
          <tbody>
          {schedules.map(schedule=>(
              <tr key={schedule.id}>
                  <td>{schedule.name}</td>
                  <td>{schedule.day_of_week}</td>
                  <td>{schedule.start_time}  {schedule.end_time}</td>
                  <td>{schedule.start_date.toString().split('T')[0]}  {schedule.end_date.toString().split('T')[0]}</td>
                  <td>{schedule.base_price}</td>
                  <td>{schedule.member_price}</td>
                  {
                    document.cookie ? <td><button onClick={() => handleClick(schedule.id)}>Enroll</button></td> : <td></td>
                  }
                  
              </tr>
          ))}

</tbody>
      </table>
    </div>
}

export default Schedules
